                            end
                        end
                        if bDebugMessages == true then LOG(sFunctionRef..': '..sBlueprint..': iCurrentTech='..iCurrentTech..'; iHighestTech='..iHighestTech) end
                        if iCurrentTech > iHighestTech then
                            iHighestTech = iCurrentTech
