                            elseif bGetFastest == true then
                                if iCurSpeed > tiHighestSpeedByTech[iCurrentTech] then tiHighestSpeedByTech[iCurrentTech] = iCurSpeed end
                            end
                        elseif bGetCheapest or bGetMostExpensive then
