            local aiBrain = oBomber:GetAIBrain()
            local tUnitsToRun = aiBrain:GetUnitsAroundPoint(iCategoriesToRun, tBombTarget, iRadiusSize, 'Ally')
            if M28Utilities.IsTableEmpty(tUnitsToRun) == false then
